/**
*
* @author       :- Christopher Johnson
* @date         :- 04-September-2011
* @dependencies :- JQuery 1.6.2
* @notes		:- This JScript will generate an id, which is unique to those generated by this plugin
*/
(function($, undefined) {
	var uniqueID = 0;
	$.fn.uniqueID = function(duration, easing, toSet, initial, delta, callback) {
		return this.attr('id','uniqueID' + uniqueID++);
	};
})(jQuery);